mapper.select:
SELECT 

FROM check_template a

WHERE a.id = #{id}

SELECT 

FROM check_template a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.template_name = #{templateName}


		AND a.used_status = #{usedStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM check_template a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE check_template SET 	
	template_name = #{templateName},
	template_desc = #{templateDesc},
	used_status = #{usedStatus},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE check_template SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
