mapper.select:
SELECT 

FROM train_content a

WHERE a.id = #{id}

SELECT
FROM train_content a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.classify = #{classify}
AND a.organizer LIKE '%'||#{organizer}||'%' '%'+#{organizer}+'%' concat('%',#{organizer},'%')
AND a.requirement = #{requirement}
ORDER BY ${page.orderBy}
ORDER BY a.sn , a.update_date DESC

SELECT 

FROM train_content a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE train_content SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
