mapper.select:
SELECT 

FROM zsk_document a

WHERE a.id = #{id}

SELECT 

FROM zsk_document a




		a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.classid = #{classid}


		AND a.file_name LIKE 
			'%'||#{fileName}||'%'
			'%'+#{fileName}+'%'
			concat('%',#{fileName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zsk_document a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE zsk_document SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
