mapper.select:
SELECT 

FROM zsk_history_version a

WHERE a.id = #{id}

SELECT 

FROM zsk_history_version a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.document_id = #{documentId}


		AND a.class_id = #{classId}


		AND a.file_name LIKE 
			'%'||#{fileName}||'%'
			'%'+#{fileName}+'%'
			concat('%',#{fileName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zsk_history_version a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE zsk_history_version SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
