mapper.select:
SELECT 

FROM check_item_check a

WHERE a.id = #{id}

SELECT , item.check_content AS checkContent, item.check_standard AS checkStandard, user.name AS checkPersonName FROM check_item_check a, check_item AS item, inspct, sys_user AS user
a.del_flag = #{DEL_FLAG_NORMAL} AND user.id = inspct.check_person AND a.check_item_id = item.id AND a.inspct_id = inspct.id
AND a.inspct_id = #{inspctId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM check_item_check a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE check_item_check SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
