mapper.select:
SELECT 

FROM check_type_class a

WHERE a.id = #{id}

SELECT 

FROM check_type_class a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.check_type_name = #{checkTypeName}


		AND a.parent_id = #{parent.id}


		AND a.parent_ids LIKE 
			'%'||#{parentIds}||'%'
			'%'+#{parentIds}+'%'
			concat('%',#{parentIds},'%')

mapper.update:
UPDATE check_type_class SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE check_type_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
