mapper.select:
SELECT 

FROM check_type_class a

WHERE a.id = #{id}

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM check_type_class a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE check_type_class SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE check_type_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} 
OR parent_ids LIKE CONCAT('%',#{id},'%')
