mapper.select:
SELECT 

FROM inspct a
WHERE a.id = #{id}

SELECT data.*,office1.name AS checkOfficeName, office2.name AS inspectionOfficeName, IFNULL(check_result.all_question_count,0) AS aLLQuestionCount, IFNULL(check_result1.solved_question_count,0) AS solvedQuestionCount FROM (SELECT , type.name As typeName, user.name AS checkUserName FROM inspct a, check_type_class AS type, sys_user AS user
a.del_flag = #{DEL_FLAG_NORMAL} AND a.type_id = type.id AND a.check_person = user.id
AND a.type_id = #{typeId}
AND a.check_dept = #{checkOffice.id}
) AS data LEFT JOIN sys_office office1 ON office1.id = data.checkOfficeId LEFT JOIN sys_office office2 ON office2.id = data.inspectionOfficeId LEFT JOIN (SELECT inspct_id, count(*) AS all_question_count FROM check_item_check WHERE check_result = '0' GROUP BY inspct_id) AS check_result ON data.id = check_result.inspct_id LEFT JOIN (SELECT check_item_check.inspct_id, count(*) AS solved_question_count FROM check_item_check, question WHERE check_item_check.id = question.check_item_check_id AND question.state_id = '5' AND check_item_check.check_result = '0' GROUP BY check_item_check.inspct_id) AS check_result1 ON data.id = check_result1.inspct_id

SELECT 

FROM inspct a

	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE inspct SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
