mapper.select:
SELECT 

FROM question a

WHERE a.id = #{id}

SELECT 

FROM question a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM question a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE question SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
