mapper.select:
SELECT 

FROM course_catalog a

WHERE a.id = #{id}

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM course_catalog a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE course_catalog SET 	
	parent_id = #{parent.id},
	catalog_name = #{catalogName},
	catalog_desc = #{catalogDesc},
	sort = #{sort}
WHERE id = #{id}

UPDATE course_catalog SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE course_catalog SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
