mapper.select:
SELECT 

FROM course_catelog a

WHERE a.id = #{id}

SELECT 

FROM course_catelog a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.attach_name = #{attachName}


		AND a.course_id = #{trainCourse.id}


		AND a.attach_id = #{attachId} 




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM course_catelog a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE course_catelog SET 	
	course_id = #{courseId},
	attach_name = #{attachName},
	attach_id = #{attachId}
WHERE id = #{id}

UPDATE course_catelog SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
