mapper.select:
SELECT 

FROM risk_access a

WHERE a.id = #{id}

SELECT 

FROM risk_access a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.number = #{number}


		AND a.years = #{years}


		AND a.unit = #{unit}


		AND a.risk_type = #{riskType}


		AND a.place_device = #{placeDevice}


		AND a.is_heaverisk = #{isHeaverisk}


		AND a.risk_level = #{riskLevel}


		AND a.duty_unit = #{dutyUnit}


		AND a.access_mothed = #{accessMothed}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM risk_access a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE risk_access SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
