mapper.select:
SELECT 

FROM train_job a

WHERE a.id = #{id}

SELECT 

FROM train_job a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.classify = #{classify}




		ORDER BY ${page.orderBy}


		ORDER BY a.sn,a.update_date DESC

SELECT 

FROM train_job a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE train_job SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
