SELECT 

FROM train_need_matrix a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.job_id = #{trainJob.id}


		AND a.content_id = #{trainContent.id}



		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM train_need_matrix a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
