mapper.select:
SELECT 

FROM train_course a

WHERE a.id = #{id}

SELECT
FROM train_course a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.course_name LIKE '%'||#{courseName}||'%' '%'+#{courseName}+'%' concat('%',#{courseName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM train_course a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO train_course( id, train_plan_extra_id, course_type_id, cover_id, doc_id, course_name, course_dec, course_time, course_count, course_grade, upload_by, upload_time, create_by, create_date, update_by, update_date, remarks, del_flag, unused_1, unused_2, unused_3
) VALUES ( #{id}, #{trainPlanExtraId}, #{courseTypeId}, #{coverId}, #{docId}, #{courseName}, #{courseDec}, #{courseTime}, #{courseCount}, #{courseGrade}, #{uploadBy}, #{uploadTime}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{remarks}, #{delFlag}, #{unused1}, #{unused2}, #{unused3}
)

mapper.update:
