	work_state_id = '45d756f45bb04155adb95e66b6a0d1c1'
WHERE id = #{id}

UPDATE work_plan SET 
	work_state_id = '8332da35c3e3419caf094f53e7a6c58c'
WHERE id = #{id}

UPDATE work_plan SET 
