mapper.select:
SELECT 

FROM work_type a

WHERE a.id = #{id}

SELECT 

FROM work_type a



		AND a.type_name = #{typeName}


		AND a.type_desc = #{typeDesc}


		AND a.parent_id = #{parent.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE work_type SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
