mapper.select:
SELECT 

FROM zsk_class a

WHERE a.id = #{id}

SELECT
FROM zsk_class a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.zsk_name LIKE '%'||#{zskName}||'%' '%'+#{zskName}+'%' concat('%',#{zskName},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.parent_ids = #{parentIds}
AND a.parent_id = #{parent.id}
ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM zsk_class a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE zsk_class SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE zsk_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
